function writeSessionCookie(b,a){if(testSessionCookie()){document.cookie=escape(b)+"="+escape(a)+"; path=/";return true}else{return false}}function writeSessionCookieWithPath(b,a){if(testSessionCookie()){document.cookie=escape(b)+"="+escape(a)+"; path="+window.location.pathname;return true}else{return false}}function getCookieValue(b){var a=new RegExp(escape(b)+"=([^;]+)");if(a.test(document.cookie+";")){a.exec(document.cookie+";");return unescape(RegExp.$1)}else{return false}}function testSessionCookie(){return true}function testPersistentCookie(){writePersistentCookie("testPersistentCookie","Enabled","minutes",1);if(getCookieValue("testPersistentCookie")=="Enabled"){return true}else{return false}}function writePersistentCookie(f,c,b,g){var d=new Date();g=g/1;var a=b;switch(a.toLowerCase()){case"years":var e=d.getYear();if(e<1000){e=e+1900}d.setYear(e+g);break;case"months":d.setMonth(d.getMonth()+g);break;case"days":d.setDate(d.getDate()+g);break;case"hours":d.setHours(d.getHours()+g);break;case"minutes":d.setMinutes(d.getMinutes()+g);break;default:alert("Invalid periodType parameter for writePersistentCookie()");break}document.cookie=escape(f)+"="+escape(c)+"; expires="+d.toGMTString()+"; path=/"}function deleteCookie(a){if(getCookieValue(a)){writePersistentCookie(a,"Pending delete","years",-1)}return true};